import android.support.v7.app.AppCompatActivity;
 import android.os.Bundle;
 import android.widget.RadioButton;
 import android.widget.RadioGroup;

 	public class MainActivity extends AppCompatActivity {
 		 RadioButton r1, r2, r3;

 		protected void onCreate(Bundle savedInstanceState) {
 		 super.onCreate(savedInstanceState);
 		 setContentView(R.layout.activity_main);
 		r1 = v.findViewById(R.id.radio1);

		 r2 = (RadioButton)findViewById(R.id.radio2);
 		 r3 = (RadioButton)findViewById(R.id.radio3);
 		 RadioGroup radiogroup = (RadioButton)findViewById(R.id.radioattendgroup);
 		radiogroup.setOnCheckedChangeListener(new RadioGroup.OnCheckedChangeListener() {
 	@Override
 		public void onCheckedChanged(RadioGroup radioGroup, int i)
 		{
 				switch (i){
 							case R.id.radio1:
 									 Toast.makeText(getContext(),"Your choice: "+r1.getText().toString(),Toast.LENGTH_SHORT).show();
 							 		break;
 							case R.id.radio2:
 									Toast.makeText(getContext(),"Your choice: "+r2.getText().toString(),Toast.LENGTH_SHORT).show();
 									break;
 							case R.id.radio3:
 									Toast.makeText(getContext(),"Your choice: "+r3.getText().toString(),Toast.LENGTH_SHORT).show();
 									break;
 							default:
 									 break;
 							}
 							}
 		});



 		return v;
 		}
 	}

import android.support.v7.app.AppCompatActivity;
 import android.os.Bundle;
 import android.widget.Button;
 import android.widget.CheckBox;
 import android.widget.Toast;
 	CheckBox c1, c2;

 	 @Override
 		 protected void onCreate(Bundle savedInstanceState) {
 			 super.onCreate(savedInstanceState);
 			 setContentView(R.layout.activity_main);
 			c1 = (CheckBox)findViewById(R.id.it);
 			c2 = (CheckBox)findViewById(R.id.cse);
 			Button b = (Button)findViewById(R.id.checkbutton);

 			b.setOnClickListener(new View.OnClickListener() {
 			@Override
 			public void onClick(View v) {
 				if(c1.isChecked())
 				 {
 				Toast.makeText(getContext(), "Congrats You got IT course", Toast.LENGTH_SHORT).show();

 				 }
 			 if(c2.isChecked())
 				 {
 				Toast.makeText(getContext(), "Congrats You got CSE course", Toast.LENGTH_SHORT).show();
 				}
 			}
 		});

 	return v;

 	}
 }
